function ret=ApeProbit(x)    	x1=x;	x1(x1<0.001)=0.001;	x1(x1>0.999)=0.999;	lx=x1;	lx(x1>0.5)=1-x1(x1>0.5);    l1 = log(1.0./lx./lx);    l2 = sqrt(l1);    l3 = (2.515517+0.802853.*l2+0.010328.*l1);    l3 = l3./(1.0+1.432788.*l2+0.189269.*l1+0.001308.*l1.*l2);    l3 = l2-l3;  	ret=l3;	ret(x1<0.5)=-l3(x1<0.5);