% ApeRegressfunction [a,aerr,b,berr,chi,dof,overflow]=ApeRegress(srtable,no_levels)	w = srtable.weight;	x = srtable.stim;	y = srtable.prob;  	snw = sum(w);	snwx =sum(w.*x);	snwy =sum(w.*y);	snwxx =sum(w.*x.*x);	snwyy =sum(w.*y.*y);	snwxy =sum(w.*x.*y);   	if  ((snwxx*snw - snwx*snwx ~= 0.0) & (snw ~= 0.0)) 		b    = (snwxy*snw - snwx*snwy)/(snwxx*snw - snwx*snwx);    	berr = sqrt(snw/(snwxx*snw - snwx*snwx));    	a    = snwy/snw - b*snwx/snw;    	aerr = sqrt(1.0/snw);    	chi  =  snwyy - snwy*snwy/snw - (snw*(snwxy - snwx*snwy/snw)*(snwxy - snwx*snwy/snw))/(snwxx*snw - snwx*snwx);    	dof  = no_levels-2;    	overflow = 0;	else     		overflow = 1; 	end